/* @requires core */

var Canvas = {};

Canvas.clear = function(el) {



};


Canvas.drawDot = function(ctx, size, xy, col) {
  Canvas.circlePath(ctx, size, xy);
  ctx.fillStyle = col;
  ctx.fill();
};

Canvas.circlePath = function(ctx, size, xy) {
  ctx.beginPath();
  ctx.arc(xy[0], xy[1], size/2, 2 * Math.PI, false);

};